type Props = {
  onConcluirTarefa: (id: number) => void;
  onRemoverTarefa: (id: number) => void;
};

const styles = StyleSheet.create({
  container: {
    marginHorizontal: 16,
  },
  tarefa: {
    flexDirection: "row",
    alignItems: "center",
    justifyContent: "space-between",
    marginVertical: 4,
  },
  tarefaTitulo: {
    fontSize: 16,
    flex: 1,
    marginRight: 8,
  },
  tarefaBotao: {
    backgroundColor: "lightgray",
    paddingHorizontal: 12,
    paddingVertical: 8,
    borderRadius: 4,
  },
  tarefaBotaoTexto: {
    color: "white",
    fontWeight: "bold",
  },
  tarefaConcluida: {
    textDecorationLine: "line-through",
  },
});
const [tarefas, setTarefas] = useState<[]>([]);
axios
  .get("http://localhost:3001/tasks")
  .then((response) => {
    console.log(response.data.title);
  })
  .catch((error) => {
    console.error(error);
  });
export const ListaTarefas = ({ onConcluirTarefa, onRemoverTarefa }: Props) => {
  return (
    <View style={styles.container}>
      {/* {tarefas.map((tarefa) => (
        <View key={} style={styles.tarefa}>
          <TouchableOpacity
            style={styles.tarefaTitulo}
            onPress={() => onConcluirTarefa(tarefa.id)}
          >
            <Text
              style={[
                styles.tarefaTitulo,
                tarefa.concluida && styles.tarefaConcluida,
              ]}
            >
              {tarefa.titulo}
            </Text>
          </TouchableOpacity>
          <TouchableOpacity
            style={styles.tarefaBotao}
            onPress={() => onRemoverTarefa(tarefa.id)}
          >
            <Text style={styles.tarefaBotaoTexto}>Remover</Text>
          </TouchableOpacity>
        </View>
      ))} */}
    </View>
  );
};
